mapper.insert:

insert into AFC_FLOW (ID, OLD_MODIFIED_TIMESTAMP, ACCOUNT_ID, 
  BALANCE, SETTLE_BALANCE, SETTLE_TIME, 
  WITHDRAWING, CASHBACK, CASHBACKING, 
  DEPOSIT, DEPOSIT_USED, MODIFIED_TIMESTAMP
  )
values (#{id,jdbcType=BIGINT}, #{oldModifiedTimestamp,jdbcType=BIGINT}, #{accountId,jdbcType=BIGINT}, 
  #{balance,jdbcType=DECIMAL}, #{settleBalance,jdbcType=DECIMAL}, #{settleTime,jdbcType=TIMESTAMP}, 
  #{withdrawing,jdbcType=DECIMAL}, #{cashback,jdbcType=DECIMAL}, #{cashbacking,jdbcType=DECIMAL}, 
  #{deposit,jdbcType=DECIMAL}, #{depositUsed,jdbcType=DECIMAL}, #{modifiedTimestamp,jdbcType=BIGINT}
  )

mapper.select:

select 

from AFC_FLOW


    and OLD_MODIFIED_TIMESTAMP = #{oldModifiedTimestamp,jdbcType=BIGINT}


    and ACCOUNT_ID = #{accountId,jdbcType=BIGINT}


    and BALANCE = #{balance,jdbcType=DECIMAL}


    and SETTLE_BALANCE = #{settleBalance,jdbcType=DECIMAL}


    and SETTLE_TIME = #{settleTime,jdbcType=TIMESTAMP}


    and WITHDRAWING = #{withdrawing,jdbcType=DECIMAL}


    and CASHBACK = #{cashback,jdbcType=DECIMAL}


    and CASHBACKING = #{cashbacking,jdbcType=DECIMAL}


    and DEPOSIT = #{deposit,jdbcType=DECIMAL}


    and DEPOSIT_USED = #{depositUsed,jdbcType=DECIMAL}


    and MODIFIED_TIMESTAMP = #{modifiedTimestamp,jdbcType=BIGINT}


select count(*) 
from AFC_FLOW
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from AFC_FLOW


    and ID = #{id,jdbcType=BIGINT}


    and OLD_MODIFIED_TIMESTAMP = #{oldModifiedTimestamp,jdbcType=BIGINT}


    and ACCOUNT_ID = #{accountId,jdbcType=BIGINT}


    and BALANCE = #{balance,jdbcType=DECIMAL}


    and SETTLE_BALANCE = #{settleBalance,jdbcType=DECIMAL}


    and SETTLE_TIME = #{settleTime,jdbcType=TIMESTAMP}


    and WITHDRAWING = #{withdrawing,jdbcType=DECIMAL}


    and CASHBACK = #{cashback,jdbcType=DECIMAL}


    and CASHBACKING = #{cashbacking,jdbcType=DECIMAL}


