    and TRADE_ID = #{tradeId,jdbcType=VARCHAR}


    and PAY_AMOUNT = #{payAmount,jdbcType=DECIMAL}


    and PAY_TIME = #{payTime,jdbcType=TIMESTAMP}


    and PAY_AMOUNT1 = #{payAmount1,jdbcType=DECIMAL}


    and PAY_AMOUNT2 = #{payAmount2,jdbcType=DECIMAL}


select count(*) 
from AFC_PAY
