select 

from AFC_REFUND


    and ACCOUNT_ID = #{accountId,jdbcType=BIGINT}


    and ORDER_ID = #{orderId,jdbcType=VARCHAR}


    and REFUND_TIME = #{refundTime,jdbcType=TIMESTAMP}


    and REFUND_TOTAL = #{refundTotal,jdbcType=DECIMAL}


    and REFUND_AMOUNT1 = #{refundAmount1,jdbcType=DECIMAL}


    and REFUND_AMOUNT2 = #{refundAmount2,jdbcType=DECIMAL}


    and REFUND_TITLE = #{refundTitle,jdbcType=VARCHAR}


    and REFUND_DETAIL = #{refundDetail,jdbcType=VARCHAR}


    and OP_ID = #{opId,jdbcType=BIGINT}


    and IP = #{ip,jdbcType=VARCHAR}


select count(*) 
from AFC_REFUND
where ID = #{id,jdbcType=BIGINT}


