mapper.select:

select 

from AFC_SETTLE_TASK


    and EXECUTE_STATE = #{executeState,jdbcType=TINYINT}


    and EXECUTE_PLAN_TIME = #{executePlanTime,jdbcType=TIMESTAMP}


    and EXECUTE_FACT_TIME = #{executeFactTime,jdbcType=TIMESTAMP}


    and ACCOUNT_ID = #{accountId,jdbcType=BIGINT}


    and TRADE_TYPE = #{tradeType,jdbcType=TINYINT}


    and TRADE_AMOUNT = #{tradeAmount,jdbcType=DECIMAL}


    and CHANGE_AMOUNT1 = #{changeAmount1,jdbcType=DECIMAL}


    and CHANGE_AMOUNT2 = #{changeAmount2,jdbcType=DECIMAL}


    and TRADE_TITLE = #{tradeTitle,jdbcType=VARCHAR}


    and TRADE_DETAIL = #{tradeDetail,jdbcType=VARCHAR}


    and ORDER_ID = #{orderId,jdbcType=VARCHAR}


    and ORDER_DETAIL_ID = #{orderDetailId,jdbcType=VARCHAR}


    and MAC = #{mac,jdbcType=VARCHAR}


    and IP = #{ip,jdbcType=VARCHAR}


select count(*) 
from AFC_SETTLE_TASK
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from AFC_SETTLE_TASK


    and ID = #{id,jdbcType=BIGINT}


